import React, {Fragment} from 'react';
import {useHistory} from 'react-router-dom';

const fs = require('fs');
const QuestionContainer = props => {
    //initialize hisotry object so that we can move on to the next part when a button is clicked
    let history = useHistory();
    
    //initializes the path to the images folder
    let imageDirectory = '../images/question container';
    let NUMBER_OF_IMAGES;
    fs.readdir(imageDirectory, (err, files) => {
        NUMBER_OF_IMAGES = files.length;
    });
    
    console.log(NUMBER_OF_IMAGES);

    //initializes the function that will allow the user to go to the confirmation page
    function goNext(e){
        history.push({
            pathname: '/confirm',
            state: e.target.alt
        })
    }
}
